function toggleElements(e){document.querySelectorAll(`${e}, .background-blur`).forEach((e=>{e.classList.contains("hidden")?(e.classList.remove("hidden"),e.classList.add("visible")):(e.classList.remove("visible"),e.classList.add("hidden"))}))}function toggleNav(e){if(window.innerWidth<=960){toggleElements(e);document.querySelector(".nav__menu-button").classList.toggle("active")}}function toggleClassBasedOnWidth(){const e=document.querySelector(".nav__links");window.innerWidth<=960?e.classList.add("hidden"):e.classList.remove("hidden")}window.addEventListener("load",toggleClassBasedOnWidth),window.addEventListener("resize",toggleClassBasedOnWidth);